import React from 'react'
import { Container, Card } from 'react-bootstrap'


export default function PokeInfo(props) {
    const flav = props.flavor_text[0].flavor_text
                    // console.log(flav)
    var desc = flav
    // desc = desc.split('\n').join(' ')
    // desc = desc.split(' ').join('%20')


    return (
        <Container className="mt-2">
            <Card className="text-center">
                <Card.Header>
                    <h5 style={{textTransform: 'uppercase'}}>{props.name}</h5>
                </Card.Header>
                <Card.Body>
                    <h6>Description:</h6>
                    <span>{desc}</span>
                </Card.Body>
            </Card>
        </Container>
    )
}
