import React from "react"
import PokeInfo from "../components/PokeInfo"


export const fetchTranslation = (desc, callback) => {
    desc = desc.split('\n').join(' ')
    desc = desc.split(' ').join('%20')

    const url = 'https://api.funtranslations.com/translate/shakespeare.json?text='

    fetch({url, json:true}, (error, {body})=>{
        const {err} = body

        if(error){
            callback('error connecting', undefined)
        }
        else if(err){
            callback(err.info, undefined)
        }
        else{
            callback(undefined, body)
        }
    })
}
