import React from 'react'
import PokeInfo from '../components/PokeInfo'
import Search from '../components/Search'
import { fetchPokemon } from '../services/getPokemon'
import { fetchTranslation } from '../services/getTranslation'
import { Spinner } from 'react-bootstrap'



export default function HomePage() {

    const [pokemon, setPokemon] = React.useState()
    const [loading, setLoading] = React.useState(false)
    const [translation, setTranslation] = React.useState()
    var desc;

    const getPokemon = async (query) => {
        setLoading(true)
        const response = await fetchPokemon(query)
        // console.log(response)
        const results = await response.json()
        // console.log(results)
        setPokemon(results)
        desc = results.flavor_text_entries[0].flavor_text
        // console.log(desc)
        const resp = await fetchTranslation(desc)
        console.log(resp)
        const res = await resp.json()
        console.log(res)
        setTranslation(res)
        setLoading(false)

    }

    // const getTranslation = async (query) => {
    //     const response = await fetchTranslation()
    //     console.log(response)
    //     const results = await response.json()
    //     console.log(results)
    //     setTranslation(results)
    //     try{
    //     console.log(pokemon.name)}
    //     catch(e){
    //         if(e){
    //             console.log('error')
    //         }
    //     }
    // }


    // var desc = 'pokemon.flavor_text_entries[0].flavor_text'

    return (
        <div>
            <Search getPokemon={getPokemon}/>
            {loading ? (<Spinner animation="border" />) :null}
            {!loading && pokemon ? (
                // <PokeInfo 
                // name={pokemon.name}
                // flavor_text={pokemon.flavor_text_entries}
                // />
                pokemon.name,
                translation.contents.translated
            ): null}

            {/* {!loading && translation && pokemon ? (
                <h5>{translation.contents.translated}</h5>
            ): null} */}
        </div>
    )
}
