import React from 'react'
// const baseUrl = 'https://pokeapi.co/api/v2'
// const query = {
//     pokemon: 'pokemon-species'
// }

// export async function fetchPokemon(pokemon) {
//     // console.log(`${baseUrl}/${query.pokemon}/${pokemon}`)
//     return fetch(`${baseUrl}/${query.pokemon}/${pokemon}`)
// }

export const fetchPokemon = (pokeName, callback)=>{
    const url = 'https://pokeapi.co/api/v2/pokemon-species/' + pokeName

    fetch({url, json:true}, (error, {body})=>{
        const {err} = body
        //error handling
        if(error){
            callback('error connecting', undefined)
        }
        else if(err){
            callback(err.info, undefined)
        }

        //send back description (english version)
        else{
            callback(undefined, body['flavor_text_entries'][1]['flavor_text'])
        }
    })
}
