import React from 'react'
import { Container, Card } from 'react-bootstrap'

export default function PokeInfo(props) {
    return (
        <Container className="mt-2">
            <Card className="text-center">
                <Card.Header>
                    <h5 style={{textTransform: 'uppercase'}}>{props.name}</h5>
                </Card.Header>
                <Card.Body>
                    <h6>Description</h6>
                    {(props.flavor_text || [])[0].map((flavor_text, key) => (
                            <div key={key}>
                            <span>{flavor_text.flavor_text}</span>
                        </div> 
                    ))}
                    {/* <div>{props.flavor_text}</div> */}
                </Card.Body>
            </Card>
        </Container>
    )
}
