import { element } from 'prop-types'
import React from 'react'
import { Container, Card } from 'react-bootstrap'


export default function PokeInfo(props) {
    const flav = props.flavor_text[0].flavor_text
                    // console.log(flav)
    var desc = flav
    desc = desc.split('\n').join(' ')
    desc = desc.split(' ').join('%20')

    const url = 'https://api.funtranslations.com/translate/shakespeare.json?text='
    const query = {
        contents: desc
    }

    async function fetchTranslation() {
    return fetch(`${url}/${desc}`)
    }

    const getTranslation = async (query) => {
        const response = await fetchTranslation()
        console.log(response)
        const results = await response.json()
        console.log(results)
        desc = results
    }

    return (
        <Container className="mt-2">
            <Card className="text-center">
                <Card.Header>
                    <h5 style={{textTransform: 'uppercase'}}>{props.name}</h5>
                </Card.Header>
                <Card.Body>
                    <h6>Description:</h6>
                    <span>{desc}</span>
                </Card.Body>
            </Card>
        </Container>
    )
}
