import React from "react"
import PokeInfo from "../components/PokeInfo"


const url = 'https://api.funtranslations.com/translate/shakespeare.json?text='
const query = {
    contents: PokeInfo.desc
}

// const desc = url + <PokeInfo.flavor_text_entries />

export async function fetchTranslation(translation) {
    return fetch(`${url}/${query.contents}/${translation}`)
}
